    fetch(`https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false`)
    .then(response => response.json())
    .then((data)=>{
      console.log(data);
      this.setState({
        discovered:data.results,
      })
    })
  }

  render(){



    return(
      <div className="app">
      <TrailersLanding 
