import React, {Component} from 'react';
import './Home.css';
import ReactLoading from 'react-loading';
import Discover from './HomeContent/Discover';


const API_KEY="16992ad7c757adb35a95a689efb88fb5";
class Home extends Component{
    state={
        discovered:[],
        onTv:[],
        loading:true,
      }
    
      componentDidMount(){
        fetch(`https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1
        `)
        .then(response => response.json())
        .then((data)=>{
          console.log(data);
          this.setState({
            discovered:data.results,
            
          })
          setTimeout(()=>
          this.setState({
            loading:false
          }),3000);
        })
        .catch(error=>{
          console.log(error);
        });
      }
   

     

      
    render(){
      
        let data;
      if(this.state.loading){
        data = <div className="loader" style={{textAlign:"center"}}>
          <ReactLoading type={"spinningBubbles"} color="#fff" height={50} width={50} />
          </div>
      }else{
        data=<div>
<Discover discovered={this.state.discovered}/>
        </div>
      }
    

        return(
            <div className='home'>
            <div className="header-one">
            <div className="narcos">
            <img src="https://www.returndates.com/backgrounds/narcos.logo.png"  alt="narcos"/>
            <div className="about">
            {this.props.title}
            <p>{this.props.description}</p>
            </div>
            <div className="a-group">
          <a href ="/" onClick={this.props.genre}>Watch Trailer</a>
          </div>
             </div>
             </div>
             <div className="content">
      
      {data}
      
      </div>
            </div>
        )
    }
};

Home.defaultProps = {
    title: 'Seasons Coming Soon!',
    description: 
    'Narcos is an American crime drama web television series. ',
  }

export default Home;
