import React, {Component} from 'react';
import './Search.css';
import $ from 'jquery';

$(document).ready(function(){
    
    $('#search').click(function(){
        $('.search').toggleClass('active');
    })
})

const API_KEY="16992ad7c757adb35a95a689efb88fb5"
class Search extends Component{
   state={
       search: "",
       searchResult:[],
   }


   async componentDidMount(){
       const data = await fetch(`https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&query=Jack+Reacher`);
       const result = await data.json();
       console.log(result);
   }

   handleSearch=(event)=>{
       this.setState({
           search:event.target.value,
           searchResult:this.state.searchResult.filter((result)=>RegExp(event.target.value, "i").exec(result.title))
       })
       console.log(this.state.search);
   }

    render(){
        return(
            <div className="search">
    <form>
        <input type="text"
         placeholder="search movies & tv..." 
         value={this.state.search}
         onChange={this.handleSearch}
    
         />
    </form>
    
            </div>
        )
    }
}
export default Search;
