class App extends Component{

  getmoviegenre = async ()=>{
    const api_call= await fetch(`https://api.themoviedb.org/3/genre/movie/list?api_key=${API_KEY}&language=en-US`);
    const genre=await api_call.json();
    console.log(genre);
  }

  render(){



    return(
      <div className="app">
      <TrailersLanding 
      name="TRAILER-X"
      title="Watch Exclusive Movie Trailers Like Never Before" 

      />

      <BrowseTrailer />
      <div className="trailers">

      </div>
      </div>

    );
  }
}
export default App;
