import React, {Component} from 'react'
import './App.css';
import ReactLoading from 'react-loading';
import {BrowserRouter as Router} from 'react-router-dom';
import TrailersLanding from './Component/TrailersLanding';
import Routes from './Component/Routes';
import Content from './Component/Content';




const API_KEY="16992ad7c757adb35a95a689efb88fb5";
class App extends Component{
  state={
    discovered:[],
    loading:true,
  }

  // getMovieGenre = async (e)=>{
  //   e.preventDefault();
  //   const api_call= await fetch(`https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1`);
  //   const data=await api_call.json();
  //   console.log(data);
  //   this.setState({
  //     image:data.result[20].poster_path,
  //     title:data.result[20].title,
  //     popularity:data.result[20].popularity,
  //     vote_average:data.result[20].vote_average,
  //   })
  // }

  componentDidMount(){
    fetch(`https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false`)
    .then(response => response.json())
    .then((data)=>{
      console.log(data);
      this.setState({
        discovered:data.results,

      })
      setTimeout(()=>
      this.setState({
        loading:false
      }),2000);
    })
    .catch(error=>{
      console.log(error);
    });
  }

  render(){


      let data;
      if(this.state.loading){
        data = <ReactLoading type={"spinnigbubbles"} color="#fff" height={200} width={200} position="center" />
      }else{
        data=<div>
<Content discovered={this.state.discovered}/>
        </div>
      }





    return(
      <div className="app">
      <Router>
        <div>
      <TrailersLanding 
      name="TRAILER-X"
      title="Watch Exclusive Movie Trailers Like Never Before"
      genre={this.getMovieGenre}

      />
      <Routes />
      </div>
      </Router>
      <div className="content">

      {data}

      </div>
      <div className="trailers">

      </div>
      </div>

    );
  }
}
export default App;
