import React, {Component} from 'react';
import './Home.css';
import ReactLoading from 'react-loading';
import Discover from './moviesContent/Discover';




const API_KEY="16992ad7c757adb35a95a689efb88fb5";
class Home extends Component{
    state={
        discovered1:[],
        discovered2:[],
        discovered3:[],
        discovered4:[],
        discovered5:[],
        discovered6:[],
        loading:true,
        isOpen:false,
        
        
      }
      
    
    
      async componentDidMount() {
        const discovered1 = await fetch(`https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&sort_by=popularity.desc`)
        const movies0 = await discovered1.json() 
        const discovered2 = await fetch(`https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=2`)
        const movies1 = await discovered2.json()
        const discovered3 = await fetch(`https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=3`)
        const movies2 = await discovered3.json()
        const discovered4 = await fetch(`https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=3`)
        const movies3 = await discovered4.json()
        const discovered5 = await fetch(`https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=3`)
        const movies4 = await discovered5.json()
        const discovered6 = await fetch(`https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=3`)
        const movies5 = await discovered6.json()
        

        this.setState({
            discovered1:movies0.results,
            discovered2:movies1.results,
            discovered3:movies2.results,
            discovered4:movies3.results,
            discovered5:movies4.results,
            discovered6:movies5.results,
        })
        setTimeout(()=>
          this.setState({
            loading:false
          }),3000);
       
    }
    openModal=()=>{
        this.setState({isOpen:true});
    }
   

      
    render(){
      
        let data;
      if(this.state.loading){
        data = <div className="loader" style={{textAlign:"center"}}>
          <ReactLoading type={"spinningBubbles"} color="#fff" height={50} width={50} />
          </div>
      }else{
        data=<div>
<Discover 
discovered1={this.state.discovered1}
discovered2={this.state.discovered2}
discovered3={this.state.discovered3}
discovered4={this.state.discovered4}
discovered5={this.state.discovered5}
discovered6={this.state.discovered6}
/>

        </div>
      }
    

        return(
            <div className='home'>
            
             <div className="content">
      
      {data}
      
      </div>
            </div>
        )
    }
};



export default Home;
