import React, {Component} from 'react'
import './App.css';
import TrailersLanding from './Component/TrailersLanding';
import BrowseTrailer from './Component/BrowseTrailer';


const API_KEY="16992ad7c757adb35a95a689efb88fb5";
class App extends Component{
  state={
    image:undefined,
    title:undefined,
    popularity:undefined,
    vote_average:undefined
  }

  getMovieGenre = async (e)=>{
    e.preventDefault();
    const api_call= await fetch(`https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1`);
    const data=await api_call.json();
    console.log(data);
    this.setState({
      image:data.result[0].poster_path,
      title:data.result[0].title,
      popularity:data.result[0].popularity,
      vote_average:data.result[0].vote_average,
    })
  }
  // componentDidMount(){
  //   fetch(`https://api.themoviedb.org/3/genre/movie/list?api_key=${API_KEY}&language=en-US`)
  //   .then(response => response.json)
  //   .then((data)=>{
  //     console.log(data);
  //   })
  // }

  render(){



    return(
      <div className="app">
      <TrailersLanding 
      name="TRAILER-X"
      title="Watch Exclusive Movie Trailers Like Never Before"
      genre={this.getMovieGenre}

      />

      <BrowseTrailer />
      <div className="trailers">

      </div>
      </div>

    );
  }
}
export default App;
