import React, {Component} from 'react';
import './Home.css';
import ReactLoading from 'react-loading';
import Trendings from './trendingContent/Trending';





const API_KEY="16992ad7c757adb35a95a689efb88fb5";
class Trending extends Component{
    state={
        //trending movies state
        trending1:[],
        trending2:[],
        trending3:[],
        trending4:[],
        trending5:[],
        trending6:[],
        trending7:[],
        trending8:[],
        trending9:[],
        trending10:[],
        trending11:[],
     
        
        loading:true,
        isOpen:false,
        
        
      }
      
    
    
      async componentDidMount() {
          //api call for trending movies from page 1 to 10
        const trending1 = await fetch(`https://api.themoviedb.org/3/movie/trending?api_key=${API_KEY}&language=en-US&page=1`)
        const trendingmovies0 = await trending1.json() 
        const trending2 = await fetch(`https://api.themoviedb.org/3/movie/trending?api_key=${API_KEY}&language=en-US&page=2`)
        const trendingmovies1 = await trending2.json()
        const trending3 = await fetch(`https://api.themoviedb.org/3/movie/trending?api_key=${API_KEY}&language=en-US&page=3`)
        const trendingmovies2 = await trending3.json()
        const trending4 = await fetch(`https://api.themoviedb.org/3/movie/trending?api_key=${API_KEY}&language=en-US&page=4`)
        const trendingmovies3 = await trending4.json()
        const trending5 = await fetch(`https://api.themoviedb.org/3/movie/trending?api_key=${API_KEY}&language=en-US&page=5`)
        const trendingmovies4 = await trending5.json()
        const trending6 = await fetch(`https://api.themoviedb.org/3/movie/trending?api_key=${API_KEY}&language=en-US&page=7`)
        const trendingmovies5 = await trending6.json()
        const trending7 = await fetch(`https://api.themoviedb.org/3/movie/trending?api_key=${API_KEY}&language=en-US&page=8`)
        const trendingmovies6 = await trending7.json()
        const trending8 = await fetch(`https://api.themoviedb.org/3/movie/trending?api_key=${API_KEY}&language=en-US&page=9`)
        const trendingmovies7 = await trending8.json()
        const trending9 = await fetch(`https://api.themoviedb.org/3/movie/trending?api_key=${API_KEY}&language=en-US&page=10`)
        const trendingmovies8 = await trending9.json()
        const trending10 = await fetch(`https://api.themoviedb.org/3/movie/trending?api_key=${API_KEY}&language=en-US&page=11`)
        const trendingmovies9 = await trending10.json()
        const trending11 = await fetch(`https://api.themoviedb.org/3/movie/trending?api_key=${API_KEY}&language=en-US&page=12`)
        const trendingmovies10 = await trending11.json()

       
        

        this.setState({
            //setstate fro trending
            trending1:trendingmovies0.results,
            trending2:trendingmovies1.results,
            trending3:trendingmovies2.results,
            trending4:trendingmovies3.results,
            trending5:trendingmovies4.results,
            trending6:trendingmovies5.results,
            trending7:trendingmovies6.results,
            trending8:trendingmovies7.results,
            trending9:trendingmovies8.results,
            trending10:trendingmovies9.results,
            trending11:trendingmovies10.results,
           

        })
        setTimeout(()=>
          this.setState({
            loading:false
          }),3000);
       
    }
    openModal=()=>{
        this.setState({isOpen:true});
    }
   

      
    render(){
      
        let data;
      if(this.state.loading){
        data = <div className="loader" style={{textAlign:"center"}}>
          <ReactLoading type={"spinningBubbles"} color="#fff" height={50} width={50} />
          </div>
      }else{
        data=<div>
            <Trendings
trending1={this.state.trending1}
trending2={this.state.trending2}
trending3={this.state.trending3}
trending4={this.state.trending4}
trending5={this.state.trending5}
trending6={this.state.trending6}
trending7={this.state.trending7}
trending8={this.state.trending8}
trending9={this.state.trending9}
trending10={this.state.trending10}
trending11={this.state.trending11}
/>



        </div>
      }
    

        return(
            <div className='trending'>
            
            
             <div className="content">
      
      {data}
      
      </div>
            </div>
        )
    }
};



export default Trending;
