import React, {Component} from 'react';
import './Home.css';
import Narcos from '/Users/Helegbedey Azeez/Desktop/React Document/Netflix-Trail/react/src/narcos.png';
import ReactLoading from 'react-loading';
import Discover from './moviesContent/Discover';
import ModalVideo from 'react-modal-video';



const API_KEY="16992ad7c757adb35a95a689efb88fb5";
class Home extends Component{
    state={
        discovered:[],
        loading:true,
        isOpen:false,
        
      }
      
    
    
      async componentDidMount() {
        const discovered = await fetch(`https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&pages=1`)
        const movies = await discovered.json() 
        const discovered1 = await fetch(`https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&pages=2`)
        const movies1 = await discovered1.json() 
        const add =[movies, movies1]
        
        console.log(movies);
        console.log(movies1);
        this.setState({
            discovered:add.results,
        })
        setTimeout(()=>
          this.setState({
            loading:false
          }),3000);
       
    }
    openModal=()=>{
        this.setState({isOpen:true});
    }

     

      
    render(){
      
        let data;
      if(this.state.loading){
        data = <div className="loader" style={{textAlign:"center"}}>
          <ReactLoading type={"spinningBubbles"} color="#fff" height={50} width={50} />
          </div>
      }else{
        data=<div>
<Discover 
discovered={this.state.discovered}
/>

        </div>
      }
    

        return(
            <div className='home'>
            
             <div className="content">
      
      {data}
      
      </div>
            </div>
        )
    }
};



export default Home;
