import React from 'react';
import './BrowseTrailer.css';

function TrailerList(props){
const discover=props.discover;

clicked=()=>{
    console.log(discover);
}
      
    const style={
        backgroundImage:`url('https://image.tmdb.org/t/p/original${props.discover.backdrop_path}')`
         };
    return(
        
        <div className="browse-movies">
        <div onClick={clicked}>Discover</div>
                   
            <div className="movie-picture" style={style}>
            <div className="fal fa-heart">{" "+ props.discover.vote_average}</div>
            </div>
            <div className="movie-details">{props.discover.title}</div>
            
        </div>
    )
};

export default TrailerList;
